(function(){"use strict";$("form div.form-group").each((_,element)=>{const $e=$(element);if($e.find("div.invalid-feedback").length){$e.find("input").addClass("is-invalid");}});function passwordConfirm($input1,$input2){const checker=e=>{console.log(e);console.log($input1.val(),$input2.val());if($input1.val()!==$input2.val()){$input1.addClass("is-invalid");$input2.addClass("is-invalid");}else{$input1.removeClass("is-invalid");$input2.removeClass("is-invalid");}};$input1.keyup(checker);$input2.keyup(checker);}
if(location.pathname==="/sign-up"){passwordConfirm($("#sign-up-form-password"),$("#sign-up-form-password-confirm"));}})();